changeSet.sql:
INSERT INTO permissions(name)
VALUES ('CLIENT_CREATE'),
       ('MERCHANT_CREATE'),
       ('PROGRAMS_CREATE'),
       ('PROCESSOR_LIST'),
       ('PRODUCT_LIST'),
       ('MERCHANT_UPDATE');

INSERT INTO roles_permissions(role_id, permission_id)
SELECT (SELECT id FROM roles WHERE name = 'SUPERVISOR' limit 1), permissions.id
FROM permissions
WHERE name IN (
    'CLIENT_CREATE'
    );
